sprites.onOverlap(SpriteKind.Player, SpriteKind.koril, function (sprite, otherSprite) {
    if (can_stun) {
        animation.stopAnimation(animation.AnimationTypes.All, otherSprite)
        scene.cameraShake(4, 500)
        otherSprite.vx = 0
        otherSprite.setKind(SpriteKind.stunnedenemy)
        music.play(music.createSoundEffect(WaveShape.Noise, 200, 1, 255, 255, 200, SoundExpressionEffect.None, InterpolationCurve.Linear), music.PlaybackMode.InBackground)
        timer.after(5000, function () {
            otherSprite.setKind(SpriteKind.koril)
            otherSprite.vx = 100
        })
    } else {
        info.changeLifeBy(-1)
    }
    pause(600)
})
