function musicplay () {
    if (replaylvl > -1 && replaylvl < 2) {
        music.play(music.createSong(hex`0078000408080105001c000f0a006400f4010a00000400000000000000000000000000000000025a000000200003939fa320003000030c189c30004000038e9a1e4000600003939fa360007000030c189c70008000038e9a1e8000a0000293a3a000b000020c9cb000c000028e1ec000e00002939fe000f000020c18f0000001028e9a`), music.PlaybackMode.LoopingInBackground)
    } else {
        if (replaylvl == 2 || (replaylvl == 5 || replaylvl == 8)) {
            music.play(music.createSong(assets.song`castle`), music.PlaybackMode.LoopingInBackground)
        } else {
            if (replaylvl > 2 && replaylvl < 5) {
                music.play(music.createSong(assets.song`Salty sea`), music.PlaybackMode.LoopingInBackground)
            } else {
                if (replaylvl > 5 && replaylvl < 8) {
                    music.play(music.createSong(assets.song`futuristic factory`), music.PlaybackMode.LoopingInBackground)
                } else {
                    if (replaylvl > 8 && replaylvl < 11) {
                        music.play(music.createSong(assets.song`Frosty Forest`), music.PlaybackMode.LoopingInBackground)
                    } else if (replaylvl > 11 && replaylvl < 14) {
                        music.play(music.createSong(assets.song`glitchy galaxy`), music.PlaybackMode.LoopingInBackground)
                    } else {
                    	
                    }
                }
            }
        }
    }
}
