sprites.onOverlap(SpriteKind.Player, SpriteKind.kingslukeyboss, function (sprite, otherSprite) {
    if (otherSprite.y < sprite.y) {
        info.changeScoreBy(1)
        boss_hits += -1
        Slimee.vy = 200
        animation.stopAnimation(animation.AnimationTypes.All, otherSprite)
        animation.runImageAnimation(
        otherSprite,
        assets.animation`kingslukeyhurted`,
        75,
        false
        )
    } else {
        info.changeLifeBy(-1)
    }
    pause(600)
})
